mapper.update:
UPDATE joinus_like
SET like_cnt = 'N'
WHERE member_id = #{member_id}
AND board_num = #{board_num}

UPDATE joinus_like
SET like_cnt = 'Y'
WHERE member_id = #{member_id}
AND board_num = #{board_num}

mapper.select:
SELECT like_cnt
FROM joinus_like
WHERE member_id = #{member_id}
AND board_num = #{board_num}
