mapper.select:
select member_nick
from joinus_member
where member_nick = #{member_nick}

select member_id
from joinus_member
where member_id = #{member_id}

SELECT COUNT(board_num)
FROM joinus_board
WHERE member_id = #{member_id}

mapper.delete:
delete from joinus_follow
where member_nick=#{member_nick}
and member_id=#{member_id}
